import React, { useState, useEffect } from 'react';
import ApiService from '../services/ApiService';
import TransactionsTable from "../components/TransactionsTable";
import {Fragment} from "@types/react";
import {Card, Form, Pagination, Table} from "react-bootstrap";

function Dashboard({user}) {
  const [customerAccount, setCustomerAccount] = useState({});
    const [isModalVisible, setIsModalVisible] = useState(false);
    const [transaction, setTransaction] = useState({ amount: 0.0, type: '',account:null });
    const [error, setError] = useState(null);

  useEffect(() => {
    ApiService.getCustomerAccount(user.user_id)
      .then((response) => {
        setCustomerAccount(response.data);
      })
      .catch((error) => console.log(error));
  }, []);

    const handleChange = (event) => {
        const { name, value } = event.target;
        setTransaction((prevState) => ({ ...prevState, [name]: value }));
    };
    const createTransaction = (transaction)=>{
        setError(null);
        ApiService.createTransaction(transaction).then((response) => {
            setCustomerAccount({ ...customerAccount, balance:response.data.balance, transactions: response.data.transactions })
            setTransaction({ amount: 0.0, type: '',account:null })
            setIsModalVisible(false);
        })
            .catch(() => {
                setError("Something went wrong, please try again!");
            });
    }
    const renderContent = () => {
        switch (activeKey) {
            case 'accounts':
                return (
                    <Fragment>
                        <h1 className="text-center mb-5">Accounts Overview</h1>
                        <Table striped bordered hover>
                            <thead>
                            <tr>
                                <th>Account ID</th>
                                <th>Balance</th>
                                <th>Actions</th>
                            </tr>
                            </thead>
                            <tbody>
                            {currentCustomers.map(customer => (
                                <tr key={customer.account_id}>
                                    <td>{customer.account_id}</td>
                                    <td>{customer.balance}</td>
                                    <td>
                                        <button
                                            style={{ background: 'transparent', border: '0' }}
                                            onClick={() => {
                                                setSelectedCustomer(customer);
                                                setIsModalVisible(true);
                                            }}
                                        >
                                            <svg
                                                xmlns="http://www.w3.org/2000/svg"
                                                width="16"
                                                height="16"
                                                fill="currentColor"
                                                className="bi bi-eye"
                                                viewBox="0 0 16 16"
                                            >
                                                <path d="M16 8s-3-5.5-8-5.5S0 8 0 8s3 5.5 8 5.5S16 8 16 8M1.173 8a13 13 0 0 1 1.66-2.043C4.12 4.668 5.88 3.5 8 3.5s3.879 1.168 5.168 2.457A13 13 0 0 1 14.828 8q-.086.13-.195.288c-.335.48-.83 1.12-1.465 1.755C11.879 11.332 10.119 12.5 8 12.5s-3.879-1.168-5.168-2.457A13 13 0 0 1 1.172 8z" />
                                                <path d="M8 5.5a2.5 2.5 0 1 0 0 5 2.5 2.5 0 0 0 0-5M4.5 8a3.5 3.5 0 1 1 7 0 3.5 3.5 0 0 1-7 0" />
                                            </svg>
                                        </button>
                                    </td>
                                </tr>
                            ))}
                            </tbody>
                        </Table>
                        <Pagination className="d-flex justify-content-center">
                            {pageNumbers.map(number => (
                                <Pagination.Item key={number} active={number === currentPage} onClick={() => paginate(number)}>
                                    {number}
                                </Pagination.Item>
                            ))}
                        </Pagination>
                    </Fragment>
                );
            case 'pendingTransactions':
                return (
                    <Fragment>
                        <h1 className="text-center mb-5">Pending Transactions</h1>
                        <TransactionsTable
                            transactions={customers.map(customer => customer.transactions).flat()
                                .filter(transaction => transaction.type === 'W' && transaction.approved === false)
                            }
                            onTransactionApproved={getAllCustomerAccounts}
                            isAgent={true}

                        />
                    </Fragment>
                );
            case 'deposits':
                return (
                    <Fragment>
                        <h1 className="text-center mb-5">Deposits overview</h1>
                        <div className="row mb-3">
                            <Form.Group className="mb-3 col-6 d-flex flex-column align-items-center">
                                <Form.Label >Start Date</Form.Label>
                                <Form.Control
                                    type="date"
                                    id="start-date"
                                    name="startDate"
                                    value={startDate}
                                    onChange={(e) => setStartDate(e.target.value)}
                                />
                            </Form.Group>
                            <Form.Group className="mb-3 col-6 d-flex flex-column align-items-center">
                                <Form.Label >End Date</Form.Label>
                                <Form.Control
                                    type="date"
                                    id="end-date"
                                    name="endDate"
                                    value={endDate}
                                    onChange={(e) => setEndDate(e.target.value)}
                                />
                            </Form.Group>
                        </div>
                        <div className="row">
                            <div className="col-6">
                                <Card>
                                    <Card.Body className="p-0">
                                        <h5 className="text-center">Deposits</h5>
                                        <h2 className="text-center"><span>{sums.deposits} €</span></h2>
                                    </Card.Body>
                                </Card>
                            </div>
                            <div className="col-6">
                                <Card style={{backgroundColor: 'lightsalmon'}}>
                                    <Card.Body className="p-0">
                                        <h5 className="text-center">Withdrawals</h5>
                                        <h2 className="text-center"><span>{sums.withdrawals} €</span></h2>
                                    </Card.Body>

                                </Card>
                            </div>


                        </div>

                    </Fragment>
                );
            default:
                return <Fragment />;
        }
    };

    return (
    <div>
      <h1>Welcome</h1>
      <p>Account balance : {customerAccount.balance}</p>
        <h1>Transactions</h1>
        <button  onClick={() => {
            setIsModalVisible(true);
        }}>+ Make a new transaction</button>
        <TransactionsTable
            transactions={customerAccount.transactions}
            isAgent={false}
        />
        {isModalVisible && (
            <div
                className="modal"
                style={{ display: 'block', backgroundColor: 'rgba(0,0,0,0.5)' }}
            >
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <button type="button" onClick={() => setIsModalVisible(false)}>
                                Close
                            </button>
                        </div>
                        <div className="modal-body">
                            <input type="number" placeholder="Transaction amount"  name="amount"
                                   value={transaction.amount}
                                   onChange={handleChange}/>
                            <button disabled={transaction.amount === 0.0} color={'orange'} type="button" onClick={() => {createTransaction({ ...transaction, type: 'W',account:customerAccount });
                                }}>
                                Withdraw
                            </button>
                            <button disabled={transaction.amount === 0.0} color={'green'} type="button" onClick={() => {createTransaction({ ...transaction, type: 'D',account:customerAccount });
                               }}>
                                Deposit
                            </button>
                            {
                                error && <span className="text-red-500 p-2">{error}</span>
                            }
                        </div>
                    </div>
                </div>
            </div>
        )}
    </div>
  );
}

export default Dashboard;
