import React, { useState } from 'react';
import ApiService from "../services/ApiService";
import {useCustomNavigate} from "../hooks/useNavigate";

function Register() {
  const [register, setRegister] = useState({ email: '', password: '', confirmPassword: '' });
  const [error, setError] = useState(null);
  const goTo = useCustomNavigate();

  const handleChange = (event) => {
    const { name, value } = event.target;
    setRegister(prevState => ({ ...prevState, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setError(null);

    if (register.password == "" || register.email== "" || register.password !== register.confirmPassword) {
      setError("Passwords do not match.");
      return;
    }

    ApiService.register({ email: register.email, password: register.password,role:'customer' })
        .then((response) => {
          goTo('/login');
        })
        .catch((error) => {
          setError("An error occurred, please try again!");
        });
  };

  return (
      <div className="w-50">
        <form onSubmit={handleSubmit} className="row d-flex">
          <input
              name="email"
              value={register.email}
              onChange={handleChange}
              className="col-12"
              type="text"
              placeholder="Email"
          />
          <input
              name="password"
              value={register.password}
              onChange={handleChange}
              className="col-12"
              type="password"
              placeholder="Password"
          />
          <input
              name="confirmPassword"
              value={register.confirmPassword}
              onChange={handleChange}
              className="col-12"
              type="password"
              placeholder="Confirm Password"
          />
          <button type="submit">Register</button>
        </form>
        {error && <span className="text-red-500 p-2">{error}</span>}
      </div>
  );
}

export default Register;
