import React, { useState, useEffect,Fragment } from 'react';
import ApiService from '../services/ApiService';
import TransactionsTable from "../components/TransactionsTable";
import {Card, ListGroup} from "react-bootstrap";

function Dashboard({user}) {
  const [customerAccount, setCustomerAccount] = useState({});
    const [isModalVisible, setIsModalVisible] = useState(false);
    const [transaction, setTransaction] = useState({ amount: 0.0, type: '',account:null });
    const [error, setError] = useState(null);
    const [activeKey, setActiveKey] = useState('overview');

  useEffect(() => {
    ApiService.getCustomerAccount(user.user_id)
      .then((response) => {
        setCustomerAccount(response.data);
      })
      .catch((error) => console.log(error));
  }, []);

    const handleChange = (event) => {
        const { name, value } = event.target;
        setTransaction((prevState) => ({ ...prevState, [name]: value }));
    };
    const createTransaction = (transaction)=>{
        setError(null);
        ApiService.createTransaction(transaction).then((response) => {
            setCustomerAccount({ ...customerAccount, balance:response.data.balance, transactions: response.data.transactions })
            setTransaction({ amount: 0.0, type: '',account:null })
            setIsModalVisible(false);
        })
            .catch(() => {
                setError("Something went wrong, please try again!");
            });
    }

    const getUnapprovedTransactions = ()=>{
        return customerAccount.transactions.filter(transaction=> !transaction.approved && transaction.type ==='W')
    }
    const renderContent = () => {
        switch (activeKey) {
            case 'overview':
                return (
                    <Fragment>
                        <h1 class="mb-5">Overview</h1>
                        <div className="row">
                            <div className="col-6">
                                <Card>
                                    <Card.Body className="p-0">
                                        <h5 className="text-center">Balance</h5>
                                        <h2 className="text-center"><span>{customerAccount.balance} €</span></h2>
                                    </Card.Body>
                                </Card>
                            </div>
                            <div className="col-6">
                                <Card style={{backgroundColor: 'lightsalmon'}}>
                                    <Card.Body className="p-0">
                                        <h5 className="text-center">No Transactions</h5>
                                        <h2 className="text-center"><span>{customerAccount.transactions.length} </span></h2>
                                    </Card.Body>

                                </Card>
                            </div>


                        </div>
                        <div className="row">
                        <div className="col-6">
                            <Card style={{backgroundColor: 'lightsalmon'}}>
                                <Card.Body className="p-0">
                                    <h5 className="text-center">Unapproved Withdrawals</h5>
                                    <h2 className="text-center"><span>{{getUnapprovedTransactions}} </span></h2>
                                </Card.Body>

                            </Card>
                        </div>
                        </div>
                    </Fragment>
                );
            case 'transactions':
                return (
                    <Fragment>
                        <h1>Transactions</h1>
                        <button  onClick={() => {
                            setIsModalVisible(true);
                        }}>+ Make a new transaction</button>
                        <TransactionsTable
                            transactions={customerAccount.transactions}
                            isAgent={false}
                        />
                    </Fragment>
                );

            default:
                return <Fragment />;
        }
    };

    return (
    <Fragment>
        <div className="d-flex h-100">
            <div className="sidebar" style={{ width: '250px' }}>
                <ListGroup className="sidebar-list">
                    <ListGroup.Item action onClick={() => setActiveKey('overview')} active={activeKey === 'overview'}>
                        Account overview
                    </ListGroup.Item>
                    <ListGroup.Item action onClick={() => setActiveKey('transactions')} active={activeKey === 'transactions'}>
                        Transactions
                    </ListGroup.Item>
                </ListGroup>
            </div>
            <div className="content flex-grow-1 p-3">
                {renderContent()}
            </div>
        </div>


        {isModalVisible && (
            <div
                className="modal"
                style={{ display: 'block', backgroundColor: 'rgba(0,0,0,0.5)' }}
            >
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <button type="button" onClick={() => setIsModalVisible(false)}>
                                Close
                            </button>
                        </div>
                        <div className="modal-body">
                            <input type="number" placeholder="Transaction amount"  name="amount"
                                   value={transaction.amount}
                                   onChange={handleChange}/>
                            <button disabled={transaction.amount === 0.0} color={'orange'} type="button" onClick={() => {createTransaction({ ...transaction, type: 'W',account:customerAccount });
                                }}>
                                Withdraw
                            </button>
                            <button disabled={transaction.amount === 0.0} color={'green'} type="button" onClick={() => {createTransaction({ ...transaction, type: 'D',account:customerAccount });
                               }}>
                                Deposit
                            </button>
                            {
                                error && <span className="text-red-500 p-2">{error}</span>
                            }
                        </div>
                    </div>
                </div>
            </div>
        )}
    </Fragment>
  );
}

export default Dashboard;
