import React, { useState, useEffect } from 'react';
import ApiService from '../services/ApiService';
import TransactionsTable from "../components/TransactionsTable";

function Dashboard({user}) {
  const [customerAccount, setCustomerAccount] = useState({});
    const [isModalVisible, setIsModalVisible] = useState(false);
    const [transaction, setTransaction] = useState({ amount: 0.0, type: '',account_id:customerAccount.account_id });
    const [error, setError] = useState(null);

  useEffect(() => {
    ApiService.getCustomerAccount(user.user_id)
      .then((response) => {
        setCustomerAccount(response.data);
      })
      .catch((error) => console.log(error));
  }, []);

    const handleChange = (event) => {
        const { name, value } = event.target;
        setTransaction((prevState) => ({ ...prevState, [name]: value }));
    };
    const createTransaction = (transaction)=>{
        setError(null);
        ApiService.createTransaction(transaction).then((response) => {
            console.log("response.data: ",response.data)
            setCustomerAccount({ ...customerAccount, balance:response.data.balance, transactions: response.data.transactions })
            setTransaction({ amount: 0.0, type: '',account_id:customerAccount.account_id })

        })
            .catch(() => {
                setError("Something went wrong, please try again!");
            });
    }
  return (
    <div>
      <h1>Welcome</h1>
      <p>Account balance : {customerAccount.balance}</p>
        <h1>Transactions</h1>
        <button  onClick={() => {
            setIsModalVisible(true);
        }}>+ Make a new transaction</button>
        <TransactionsTable
            transactions={customerAccount.transactions}
            isAgent={false}
        />
        {isModalVisible && (
            <div
                className="modal"
                style={{ display: 'block', backgroundColor: 'rgba(0,0,0,0.5)' }}
            >
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <button type="button" onClick={() => setIsModalVisible(false)}>
                                Close
                            </button>
                        </div>
                        <div className="modal-body">
                            <input type="number" placeholder="Transaction amount"  name="amount"
                                   value={transaction.amount}
                                   onChange={handleChange}/>
                            <button disabled={transaction.amount === 0.0} color={'orange'} type="button" onClick={() => {createTransaction({ ...transaction, type: 'W' });
                                }}>
                                Withdraw
                            </button>
                            <button disabled={transaction.amount === 0.0} color={'green'} type="button" onClick={() => {createTransaction({ ...transaction, type: 'D' });
                               }}>
                                Deposit
                            </button>
                            {
                                error && <span className="text-red-500 p-2">{error}</span>
                            }
                        </div>
                    </div>
                </div>
            </div>
        )}
    </div>
  );
}

export default Dashboard;
