import React, { useState, useEffect } from 'react';
import ApiService from '../services/ApiService';
import TransactionsTable from "../components/TransactionsTable";

function Dashboard({user}) {
  const [customerAccount, setCustomerAccount] = useState({});
    const [isModalVisible, setIsModalVisible] = useState(false);
    const [transaction, setTransaction] = useState({ amount: '', password: '' });

  useEffect(() => {
    ApiService.getCustomerAccount(user.user_id)
      .then((response) => {
        setCustomerAccount(response.data);
      })
      .catch((error) => console.log(error));
  }, []);

  return (
    <div>
      <h1>Welcome</h1>
      <p>Account balance : {customerAccount.balance}</p>
        <h1>Transactions</h1>
        <button  onClick={() => {
            setIsModalVisible(true);
        }}>+ Make a new transaction</button>
        <TransactionsTable
            transactions={customerAccount.transactions}
            isAgent={false}
        />
        {isModalVisible && (
            <div
                className="modal"
                style={{ display: 'block', backgroundColor: 'rgba(0,0,0,0.5)' }}
            >
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <button type="button" onClick={() => setIsModalVisible(false)}>
                                Close
