import React from 'react';
import ApiService from "../services/ApiService";

function TransactionsTable({ transactions,onTransactionApproved }) {

  const approveTransaction = (transaction_id)=>{
        ApiService.approveTransaction(transaction_id).then((response) => {
            console.log("response: ",response);
            if (response && onTransactionApproved) {
                onTransactionApproved();
            }
        })
            .catch((error) => console.log(error));
    }
  return (
    <table className="table">
      <thead>
        <tr>
          <th>Date</th>
          <th>ID</th>
          <th>Amount</th>
          <th>Type</th>
            <th>Status</th>
            <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {transactions.map((transaction) => (
          <tr key={transaction.transaction_id}>
            <td>{transaction.created_at}</td>
            <td>{transaction.transaction_id}</td>
            <td>{transaction.amount}</td>
            <td>{transaction.type}</td>
            <td>{transaction.type === 'W'?  transaction.approved ? "Approved":"Not-Approved" : "-"}</td>
              { transaction.type === 'W' && !transaction.approved &&
                  <td>
                      <button
                          style={{ background: 'transparent', border: '0' }}
                          onClick={() => {
                              approveTransaction(transaction.transaction_id)
                          }}
                      >
                          <svg xmlns="http://www.w3.org/2000/svg" color={'green'} width="16" height="16" fill="currentColor"
                               className="bi bi-check2" viewBox="0 0 16 16">
                              <path
                                  d="M13.854 3.646a.5.5 0 0 1 0 .708l-7 7a.5.5 0 0 1-.708 0l-3.5-3.5a.5.5 0 1 1 .708-.708L6.5 10.293l6.646-6.647a.5.5 0 0 1 .708 0"/>
                          </svg>
                      </button>
                  </td>
              }
          </tr>
        ))}
      </tbody>
    </table>
  );
}

export default TransactionsTable;
