import React, {useState, useContext} from 'react';
import { LoginContext } from '../context/LoginContext';
import ApiService from "../services/ApiService";

function Login() {
  const [login, setLogin] = useState({ email: '', password: '' });
  const { dispatch } = useContext(LoginContext);
  const [error, setError] = useState(null);
  const handleChange = (event) => {
    const { name, value } = event.target;
    setLogin((prevState) => ({ ...prevState, [name]: value }));
  };



  const handleSubmit = (e) => {
    e.preventDefault();
      setError(null);

      ApiService.getUser({"email":login.email,"password":login.password})
        .then((response) => {
          const user = response.data;
            localStorage.setItem('user', JSON.stringify(user));
            dispatch({ type: 'LOGIN', payload: user });

        })
        .catch(() => {
            setError("These credentials are incorrect, please try again!");
        });


  };

  return (
    <div className="w-50">
      <form onSubmit={handleSubmit} className="row d-flex">
        <input
          name="email"
          value={login.email}
          onChange={handleChange}
          className="col-12"
          type="text"
          placeholder="Email"
        />
        <input
          name="password"
          value={login.password}
          onChange={handleChange}
          className="col-12"
          type="password"
          placeholder="Password"
        />
        <button type="submit">Login</button>
      </form>
      {
          error && <span className="text-red-500 p-2">{error}</span>
      }
    </div>
  );
}

export default Login;
